CREATE OR REPLACE FUNCTION obter_se_setor_visualiza_hist ( nr_seq_historico_p bigint, cd_setor_p bigint, ie_visualiza_inclui_p text default 'V', nm_usuario_p text default null) RETURNS varchar AS $body$
DECLARE


/*ie_visualiza_inclui_p
I - Incluindo
V - Visualizando
*/
ds_retorno_w			varchar(1)	:= 'S';
nr_seq_tipo_w			bigint;
cd_cargo_w			bigint	:= 0;
nm_usuario_w			varchar(15);

c01 CURSOR FOR
	SELECT	ie_visualiza
	from	man_tipo_hist_setor
	where	nr_seq_tipo_hist = nr_seq_tipo_w
	and	coalesce(cd_setor_atendimento,coalesce(cd_setor_p,0)) = coalesce(cd_setor_p,0)
	and	coalesce(cd_cargo,coalesce(cd_cargo_w,0))	= coalesce(cd_cargo_w,0)
	and	coalesce(nm_usuario_regra,coalesce(nm_usuario_w,'X')) = coalesce(nm_usuario_w,'X')
	order by nm_usuario_regra desc,
		cd_cargo desc,
		cd_setor_atendimento desc,
		nr_sequencia;


BEGIN

begin
select	nr_seq_tipo
into STRICT	nr_seq_tipo_w
from	man_ordem_serv_tecnico
where	nr_sequencia	= nr_seq_historico_p;
exception when others then
	nr_seq_tipo_w	:= 0;
end;

if (nr_seq_tipo_w > 0) then
	begin
	nm_usuario_w	:= coalesce(nm_usuario_p,wheb_usuario_pck.get_nm_usuario);

	begin
	cd_cargo_w := (obter_dados_usuario_opcao(nm_usuario_w,'R'))::numeric;
	exception when others then
		cd_cargo_w	:= 0;
	end;

	open C01;
	loop
	fetch C01 into
		ds_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
	end;
end if;

if (ds_retorno_w = 'V') and (ie_visualiza_inclui_p = 'I') then
	ds_retorno_w := 'N';
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
