SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_sla_nao_prev (nm_usuario_prev_p text, nm_usuario_cor_p text, nr_seq_ordem_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_os_consistir_w	varchar(255);
qt_w				integer;
qt_2w				integer;
nr_seq_ordem_w		bigint;

c01 CURSOR FOR 
SELECT	a.nr_sequencia /* Verificar se o usuário tem alguma SLA não prevista */
 
from	man_ordem_serv_programar_v a 
where	a.nm_usuario_prev		= nm_usuario_prev_p 
and		coalesce(substr(man_obter_se_mostra_os_confid(a.nr_sequencia,nm_usuario_cor_p),1,1),'S') = 'S' 
and 	a.ie_tipo_os in ('SDP','SAP','ATR') 
and		not exists (SELECT	1 
					from 	MAN_ORDEM_ATIV_PREV b 
					where 	a.nr_sequencia = b.NR_SEQ_ORDEM_SERV 
					and		b.nm_usuario_prev		= nm_usuario_prev_p 
					and		trunc(b.DT_PREVISTA,'dd') >= trunc(clock_timestamp(),'dd')) 
and		a.nr_seq_estagio <> 31 
and		exists (select	1 
				from	man_ordem_serv_sla x 
				where	x.nr_seq_ordem = a.nr_sequencia) 
and  	exists (	select	1 
					from   	man_ordem_servico_exec	x 
		where	x.nr_seq_ordem	= a.nr_sequencia 
		and		x.nm_usuario_exec	= nm_usuario_prev_p 
		and		coalesce(x.dt_fim_execucao::text, '') = '');


BEGIN 
 
select	count(*) 
into STRICT	qt_w 
from	man_ordem_serv_sla x 
where	x.nr_seq_ordem = nr_seq_ordem_p;
 
if (qt_w = 0) then /* Verificar se a ordem selecionada não é uma SLA */
 
 
	select	count(*) 
	into STRICT	qt_2w 
	from	man_ordem_servico x 
	where	x.nr_sequencia	= nr_seq_ordem_p 
	and		ie_classificacao = 'E';
 
	if (qt_2w = 0) then /* Verifica se a ordem não é Defeito */
 
 
		open C01;
		loop 
		fetch C01 into	 
			nr_seq_ordem_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			if (nr_os_consistir_w IS NOT NULL AND nr_os_consistir_w::text <> '') then 
				nr_os_consistir_w	:= nr_os_consistir_w || ', ' || nr_seq_ordem_w;
			else 
				nr_os_consistir_w	:= nr_seq_ordem_w;
			end if;
		end loop;
		close C01;
 
	end if;
end if;
 
return	nr_os_consistir_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_sla_nao_prev (nm_usuario_prev_p text, nm_usuario_cor_p text, nr_seq_ordem_p bigint) FROM PUBLIC;

