CREATE OR REPLACE FUNCTION obter_se_sne_continua_vipe ( nr_prescricao_p bigint, nr_atendimento_p bigint, dt_horario_p timestamp, dt_inicial_p timestamp, dt_final_p timestamp, cd_material_p bigint, qt_dose_p bigint, cd_intervalo_p text, ie_considera_horario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1) := 'S';
dt_inicial_w			timestamp;
dt_final_w			timestamp;


BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then
	begin

	dt_inicial_w	:= dt_inicial_p - 3;
	dt_final_w	:= dt_final_p + 3;

	select	coalesce(max('N'),'S')
	into STRICT	ie_retorno_w
	from	prescr_material b,
		prescr_medica c
	where	c.nr_prescricao = b.nr_prescricao
	and	c.dt_validade_prescr between dt_inicial_w and dt_final_w
	and	c.nr_atendimento = nr_atendimento_p
	and	c.nr_prescricao > nr_prescricao_p
	and	b.cd_material	= cd_material_p
	and	b.ie_agrupador	= 8
	and	b.qt_dose	= qt_dose_p
	and	b.cd_intervalo	= cd_intervalo_p
	and	((b.ie_status in ('I','INT')) or
		 ((obter_se_acm_sn(b.ie_acm,b.ie_se_necessario) = 'N') and (dt_horario_p between dt_inicial_p and dt_final_p)))
	and	obter_se_prescr_vig_adep(c.dt_inicio_prescr,c.dt_validade_prescr,dt_inicial_p,dt_final_p) = 'S';

	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
