CREATE OR REPLACE FUNCTION obter_se_sne_continua ( nr_prescricao_p bigint, cd_material_p bigint, qt_dose_p bigint, cd_unidade_medida_p text, cd_intervalo_p text, ie_via_aplicacao_p text, dt_inicio_p timestamp, dt_fim_p timestamp ) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1) := 'N';
nr_seq_solucao_w		integer;
ie_status_w			varchar(3);
nr_atendimento_w		bigint;
nr_prescricao_w			bigint;

c01 CURSOR FOR
SELECT	substr(plt_obter_status_solucao(2,a.nr_prescricao,a.nr_sequencia),1,3),
	a.nr_prescricao
from	prescr_material a,
	prescr_medica b
where	a.nr_prescricao	= b.nr_prescricao
and	b.nr_atendimento = nr_atendimento_w
and	a.cd_material	= cd_material_p
and	coalesce(a.qt_dose,coalesce(qt_dose_p,0))	= coalesce(qt_dose_p,0)
and	coalesce(a.cd_unidade_medida_dose,coalesce(cd_unidade_medida_p,'XPTO'))	= coalesce(cd_unidade_medida_p,'XPTO')
and	coalesce(a.cd_intervalo,coalesce(cd_intervalo_p,'XPTO'))	= coalesce(cd_intervalo_p,'XPTO')
and	coalesce(a.ie_via_aplicacao,coalesce(ie_via_aplicacao_p,'XPTO'))	= coalesce(ie_via_aplicacao_p,'XPTO')
and	a.ie_agrupador	= 8
and	b.dt_validade_prescr between(clock_timestamp() - interval '10 days') and (clock_timestamp() + interval '10 days')
and	b.nr_prescricao < nr_prescricao_p
and	coalesce(a.ie_suspenso,'N') = 'N'
and	obter_se_prescr_vig_adep(b.dt_inicio_prescr,b.dt_validade_prescr,dt_inicio_p,dt_fim_p) = 'S'
order by 	a.nr_prescricao desc;


BEGIN

select	max(nr_atendimento)
into STRICT	nr_atendimento_w
from	prescr_medica
where	nr_prescricao	= nr_prescricao_p;

open C01;
loop
fetch C01 into
	ie_status_w,
	nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_status_w <> 'T') then
		select 	coalesce(max('N'),'S')
		into STRICT	ie_retorno_w
		from 	prescr_medica
		where	nr_prescricao = nr_prescricao_w
		and	dt_validade_prescr < clock_timestamp();
		exit;
	else
		ie_retorno_w := 'N';
	end if;
	end;
end loop;
close C01;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
