CREATE OR REPLACE FUNCTION obter_se_sobra_compativel ( nr_seq_ordem_p bigint) RETURNS varchar AS $body$
DECLARE

ie_retorno_w	varchar(1) := 'N';




BEGIN
if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') then
	select coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	FROM can_ordem_item_prescr f, can_ordem_prod e, far_etapa_producao d, material c, quimio_sobra_overfill a
LEFT OUTER JOIN material_lote_fornec b ON (a.nr_seq_lote_fornec = b.nr_sequencia)
WHERE f.nr_seq_ordem = e.nr_sequencia and e.nr_sequencia = nr_seq_ordem_p and d.nr_sequencia = e.nr_seq_etapa_prod  and coalesce(dt_estabilidade,clock_timestamp() + interval '1 days') > clock_timestamp() and a.cd_material	     = c.cd_material and ((a.cd_material = f.cd_material)  or
		(c.nr_seq_ficha_tecnica = (	SELECT  max(x.nr_seq_ficha_tecnica)
						from    material x
						where	x.cd_material = f.cd_material))or (	f.cd_material = c.cd_material_generico)) and qt_saldo	> 0 and (ie_tipo in ('S', 'SD'));
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
