CREATE OR REPLACE FUNCTION obter_se_sobreposicao_horario (cd_agenda_p bigint, dt_agenda_p timestamp, nr_min_duracao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_sobreposicao_w		varchar(1) := 'N';
hr_param_w			timestamp;
nr_seq_horario_w		bigint;
hr_agenda_w			timestamp;
ds_lista_status_w 		varchar(255)	:= null;
ie_sobreposicao_encaixe_w	varchar(1) := 'S';


BEGIN

select	coalesce(max(ie_sobreposicao_encaixe),'S')
into STRICT	ie_sobreposicao_encaixe_w
from	parametro_agenda
where	cd_estabelecimento = obter_estabelecimento_ativo;

if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (dt_agenda_p IS NOT NULL AND dt_agenda_p::text <> '') and (nr_min_duracao_p IS NOT NULL AND nr_min_duracao_p::text <> '') then

	if (wheb_usuario_pck.get_cd_funcao = 871) then /*so ira buscar o parametro se estiver na funcao Gestao da Agenda cirurgica*/
		ds_lista_status_w := Obter_Param_Usuario(871, 820, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ds_lista_status_w);	
	end if;
	
	/* obter horario parametro */

	hr_param_w := dt_agenda_p + nr_min_duracao_p / 1440;

	/* obter horario seguinte */

	if (coalesce(ds_lista_status_w,'XPTO') = 'XPTO') then
		select	min(hr_inicio)
		into STRICT	hr_agenda_w
		from	agenda_paciente
		where	cd_agenda = cd_agenda_p
		and	dt_agenda = trunc(dt_agenda_p,'dd')
		and	hr_inicio > dt_agenda_p
		and	ie_status_agenda not in ('C','L','II')
		and	((ie_sobreposicao_encaixe_w = 'N' and coalesce(ie_encaixe,'N') <> 'S' ) or ie_sobreposicao_encaixe_w = 'S');
	else	
		select	min(hr_inicio)
		into STRICT	hr_agenda_w
		from	agenda_paciente
		where	cd_agenda = cd_agenda_p
		and		dt_agenda = trunc(dt_agenda_p,'dd')
		and		hr_inicio > dt_agenda_p
		and 	obter_se_contido_char(ie_status_agenda,ds_lista_status_w || ',L') = 'N';
	end if;	

	/* verificar sobreposicao */

	if (hr_agenda_w IS NOT NULL AND hr_agenda_w::text <> '') then

		if ( hr_param_w > hr_agenda_w ) then
		
			ie_sobreposicao_w := 'S';
		else
			ie_sobreposicao_w := 'N';

		end if;
	else
		ie_sobreposicao_w := 'N';
	end if;

end if;

return ie_sobreposicao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
