CREATE OR REPLACE FUNCTION obter_se_solic_atrasado ( nr_solic_compra_p solic_compra_item.nr_solic_compra%TYPE ) RETURNS varchar AS $body$
DECLARE

ie_atrasado_w           varchar(1) := 'N';
nr_item_solic_compra_w	solic_compra_item.nr_item_solic_compra%TYPE;

BEGIN
SELECT	MAX(nr_item_solic_compra)
INTO STRICT	nr_item_solic_compra_w
FROM	solic_compra_item
WHERE	nr_solic_compra = nr_solic_compra_p;

SELECT	coalesce(MAX('S'), 'N')
INTO STRICT	ie_atrasado_w
FROM	ordem_compra
WHERE	nr_ordem_compra		=  obter_ordem_solic_compra(nr_solic_compra_p, nr_item_solic_compra_w)
AND	coalesce(dt_baixa::text, '') = ''
AND	dt_entrega		<  clock_timestamp();

RETURN	ie_atrasado_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
