CREATE OR REPLACE FUNCTION obter_se_solic_em_cotacao ( nr_solic_compra_p bigint) RETURNS varchar AS $body$
DECLARE


qt_existe_w	bigint;
ie_existe_w	varchar(1) := 'N';


BEGIN

select	count(*)
into STRICT	qt_existe_w
from (
	SELECT	1
	from	cot_compra a,
		cot_compra_item b
	where	a.nr_cot_compra = b.nr_cot_compra
	and	nr_solic_compra = nr_solic_compra_p
	and	coalesce(a.nr_seq_motivo_cancel::text, '') = ''
	
union all

	SELECT	1
	from	cot_compra a,
		cot_compra_item b,
		cot_compra_solic_agrup c
	where	a.nr_cot_compra = b.nr_cot_compra
	and	b.nr_cot_compra = c.nr_cot_compra
	and	b.nr_item_cot_compra = c.nr_item_cot_compra
	and	c.nr_solic_compra = nr_solic_compra_p
	and	coalesce(a.nr_seq_motivo_cancel::text, '') = '') alias3;

if (qt_existe_w > 0) then
	ie_existe_w := 'S';
end if;

return	ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
