CREATE OR REPLACE FUNCTION obter_se_solic_em_ordem (nr_solic_compra_p bigint) RETURNS varchar AS $body$
DECLARE

	 
ds_retorno_w		varchar(1)	:= 'N';
qt_existe_ordem_w	bigint	:= 0;

 

BEGIN 
 
select	count(1) 
into STRICT	qt_existe_ordem_w 
from	ordem_compra a, 
	ordem_compra_item b 
where	a.nr_ordem_compra = b.nr_ordem_compra 
and	coalesce(a.nr_seq_motivo_cancel::text, '') = '' 
and	b.nr_solic_compra = nr_solic_compra_p;
 
if (qt_existe_ordem_w >= 1) then 
	ds_retorno_w	:= 'S';
else 
	select	count(1) 
	into STRICT	qt_existe_ordem_w 
	from	cot_compra_solic_agrup 
	where	nr_ordem_compra > 0 
	and	nr_solic_compra = nr_solic_compra_p 
	and	obter_se_oc_cancelado(nr_ordem_compra) = 'N';
	 
	if (qt_existe_ordem_w >= 1) then 
		ds_retorno_w	:= 'S';
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
