CREATE OR REPLACE FUNCTION obter_se_solic_entrega_parcial (nr_solic_compra_p bigint) RETURNS varchar AS $body$
DECLARE


ie_entregue_parcial_w		varchar(1) := 'N';
qt_material_w			double precision := 0;
qt_entregue_w			double precision := 0;


BEGIN

select 	sum(coalesce(obter_qt_entregue_solic_nf(nr_solic_compra, nr_item_solic_compra),0)),
	sum(qt_material)
into STRICT 	qt_entregue_w,
	qt_material_w
from 	solic_compra_item
where 	nr_solic_compra = nr_solic_compra_p
and 	obter_ordem_solic_compra(nr_solic_compra_p, nr_item_solic_compra) > 0;

if (qt_entregue_w > 0) and (qt_entregue_w < qt_material_w) then
	ie_entregue_parcial_w := 'S';
end if;

return	ie_entregue_parcial_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
