SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_solic_laudo ( nr_atendimento_p bigint, ie_classificacao_p bigint, cd_medico_solic_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'S';
qt_regra_w		bigint;
qt_existe_w		bigint;

ie_grupo_laudo_w	smallint;
ie_regra_W		varchar(1);
cd_medico_W		varchar(10);
cd_convenio_w		integer;

C01 CURSOR FOR
	SELECT	ie_regra,
		cd_medico
	from	sus_regra_solic_laudo
	where	ie_grupo_laudo in (1,9)
	order by 1,2;

C02 CURSOR FOR
	SELECT	ie_regra,
		cd_medico
	from	sus_regra_solic_laudo
	where	ie_grupo_laudo in (2,9)
	order by 1,2;


BEGIN

if (ie_classificacao_p IS NOT NULL AND ie_classificacao_p::text <> '') then

	select	obter_convenio_atendimento(nr_atendimento_p)
	into STRICT	cd_convenio_w
	;

	if (ie_classificacao_p = 1) then
		select	count(*)
		into STRICT	qt_regra_w
		from	sus_regra_solic_laudo
		where	ie_grupo_laudo in (1,9);

		if (qt_regra_w > 0) then
			ie_retorno_w	:= 'N';
			OPEN C01;
			LOOP
			FETCH C01 into
				ie_regra_W,
				cd_medico_W;
		    	EXIT WHEN NOT FOUND; /* apply on C01 */

			if (ie_regra_W = 'T') then
				ie_retorno_w	:= 'S';
				exit;
			elsif (ie_regra_W = 'I') and (cd_medico_solic_p = cd_medico_W) then
				ie_retorno_w	:= 'S';
				exit;
			elsif (ie_regra_W = 'C') then
				select	count(*)
				into STRICT	qt_existe_w
				from	medico_convenio
				where	cd_convenio		= cd_convenio_w
				and	cd_pessoa_fisica	= cd_medico_solic_p
				and	ie_conveniado		= 'S';

				if (qt_existe_w > 0) then
					ie_retorno_w	:= 'S';
					exit;
				end if;
			end if;
			END LOOP;
			CLOSE C01;
		end if;
	end if;

	if (ie_classificacao_p in (11,12)) then
		select	count(*)
		into STRICT	qt_regra_w
		from	sus_regra_solic_laudo
		where	ie_grupo_laudo in (2,9);

		if (qt_regra_w > 0) then
			ie_retorno_w	:= 'N';
			OPEN C02;
			LOOP
			FETCH C02 into
				ie_regra_W,
				cd_medico_W;
		    	EXIT WHEN NOT FOUND; /* apply on C02 */

			if (ie_regra_W = 'T') then
				ie_retorno_w	:= 'S';
				exit;
			elsif (ie_regra_W = 'I') and (cd_medico_solic_p = cd_medico_W) then
				ie_retorno_w	:= 'S';
				exit;
			elsif (ie_regra_W = 'C') then
				select	count(*)
				into STRICT	qt_existe_w
				from	medico_convenio
				where	cd_convenio		= cd_convenio_w
				and	cd_pessoa_fisica	= cd_medico_solic_p
				and	ie_conveniado		= 'S';

				if (qt_existe_w > 0) then
					ie_retorno_w	:= 'S';
					exit;
				end if;
			end if;
			END LOOP;
			CLOSE C02;
		end if;
	end if;

end if;


return ie_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
