CREATE OR REPLACE FUNCTION obter_se_solic_nao_integrado ( nr_solic_compra_p bigint) RETURNS varchar AS $body$
DECLARE




qt_itens_solic_w			integer;
qt_itens_integrados_w			integer;
qt_itens_nao_integrados_w		integer;
ie_solic_possui_n_integrado_w		varchar(1) := 'N';	--Verifica se a solicitação possui integrados e não integrados na mesma solicitação;
BEGIN

select	count(*)
into STRICT	qt_itens_solic_w
from	solic_compra_item
where 	nr_solic_compra = nr_solic_compra_p;

select	count(*)
into STRICT	qt_itens_integrados_w
from	solic_compra_item
where	nr_solic_compra = nr_solic_compra_p
and	obter_se_integra_sc_item(nr_solic_compra_p,cd_material) = 'S';

select	count(*)
into STRICT	qt_itens_nao_integrados_w
from	solic_compra_item
where	nr_solic_compra = nr_solic_compra_p
and	obter_se_integra_sc_item(nr_solic_compra_p,cd_material) = 'N';


if (qt_itens_nao_integrados_w > 0) and (qt_itens_integrados_w > 0) then
	ie_solic_possui_n_integrado_w := 'S';
end if;

return	ie_solic_possui_n_integrado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_solic_nao_integrado ( nr_solic_compra_p bigint) FROM PUBLIC;

