SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_solic_ordem_reprov ( nr_documento_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/* Retorna S ou N se a Solicitação de compras ou Ordem de compra foi reprovada pelo processo de aprovação
 IE_OPCAO_P
	0 - Solicitação de Compras
	1 - Ordem de Compra*/
qt_existe_w			bigint;
ie_reprovado_w		varchar(1) := 'N';


BEGIN

if (nr_documento_p IS NOT NULL AND nr_documento_p::text <> '') and (ie_opcao_p = 0) then
	select	count(*)
	into STRICT	qt_existe_w
	from	solic_compra_item
	where	nr_solic_compra = nr_documento_p
	and	(dt_reprovacao IS NOT NULL AND dt_reprovacao::text <> '');
end if;

if (nr_documento_p IS NOT NULL AND nr_documento_p::text <> '') and (ie_opcao_p = 1) then
	select	count(*)
	into STRICT	qt_existe_w
	from	ordem_compra_item
	where	nr_ordem_compra = nr_documento_p
	and	(dt_reprovacao IS NOT NULL AND dt_reprovacao::text <> '');
end if;

if (qt_existe_w > 0) then
	ie_reprovado_w := 'S';
end if;

return	ie_reprovado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_solic_ordem_reprov ( nr_documento_p bigint, ie_opcao_p bigint) FROM PUBLIC;

