CREATE OR REPLACE FUNCTION obter_se_solic_vaga (cd_pessoa_fisica_p text ) RETURNS varchar AS $body$
DECLARE

 
ie_possui_reserva_w	varchar(1);


BEGIN 
 
select	coalesce(max('S'),'N') 
into STRICT	ie_possui_reserva_w 
from	gestao_vaga 
where	cd_pessoa_fisica	=	cd_pessoa_fisica_p 
and	dt_solicitacao 		<= clock_timestamp() 
and	ie_status in ('P','A') 
and	((coalesce(nr_atendimento::text, '') = '') or ((nr_atendimento IS NOT NULL AND nr_atendimento::text <> '') and OBTER_SE_ATENDIMENTO_ALTA(nr_atendimento) = 'N' ));
 
return	ie_possui_reserva_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
