CREATE OR REPLACE FUNCTION obter_se_solucao_apap ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_ficha_tecnica_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1)	:= 'N';
qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	material c,
	prescr_material b,
	prescr_medica a
where	a.nr_prescricao		= b.nr_prescricao
and	a.nr_prescricao		= nr_prescricao_p
and	b.nr_sequencia_solucao	= nr_seq_solucao_p
and	b.cd_material		= c.cd_material
and	c.nr_seq_ficha_tecnica	= nr_ficha_tecnica_p;

if (qt_existe_w > 0) then
	begin
	ds_retorno_w	:= 'S';
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
