CREATE OR REPLACE FUNCTION obter_se_solucao_instalada (nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_etapa_p bigint) RETURNS varchar AS $body$
DECLARE


ie_instalada_w	varchar(1);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') and (nr_etapa_p IS NOT NULL AND nr_etapa_p::text <> '') then

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_instalada_w
	from	adep_processo
	where	nr_prescricao	= nr_prescricao_p
	and	nr_seq_solucao	= nr_seq_solucao_p
	and	nr_etapa		= nr_etapa_p
	and	ie_status_processo	= 'A';

end if;

return ie_instalada_w;

end;
$body$
