CREATE OR REPLACE FUNCTION obter_se_solucao_vencida (dt_inicio_prescr_p timestamp, nr_horas_validade_p bigint default 0) RETURNS varchar AS $body$
DECLARE


ie_vencida_w		varchar(1) := 'N';
nr_horas_duracao_w	bigint;


BEGIN

if (nr_horas_validade_p > 0) then

	nr_horas_duracao_w	:= Obter_Hora_Entre_datas(clock_timestamp(), dt_inicio_prescr_p);

	if	((dt_inicio_prescr_p + (nr_horas_validade_p/24)) < clock_timestamp()) then
		ie_vencida_w := 'S';
	end if;

end if;

return	ie_vencida_w;

end;
$body$
LANGUAGE PLPGSQL
