CREATE OR REPLACE FUNCTION obter_se_sol_acm_sn ( nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_acm_sn_w	varchar(1) := 'N';
ie_acm_w	varchar(1);
ie_sn_w		varchar(1);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then
	begin
	select	coalesce(max(ie_acm),'N'),
		coalesce(max(ie_se_necessario),'N')
	into STRICT	ie_acm_w,
		ie_sn_w
	from	prescr_solucao
	where	nr_prescricao	= nr_prescricao_p
	and	nr_seq_solucao	= nr_seq_solucao_p
	and	coalesce(IE_ETAPA_ESPECIAL,'N')	= 'N';

	if (ie_acm_w = 'S') or (ie_sn_w = 'S') then
		begin
		ie_acm_sn_w	:= 'S';
		end;
	end if;
	end;
end if;
return ie_acm_sn_w;
end;
$body$
