SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_sol_adm ( nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
qt_etapas_pend_w	bigint	:= 0;
ie_acm_w			varchar(1);
ie_se_necessario_w	varchar(1);
ie_status_w			varchar(15);
nr_etapas_w			bigint;



BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then

	select 	max(coalesce(ie_acm,'N')),
			max(coalesce(ie_se_necessario,'N')),
			max(nr_etapas),
			max(ie_status)
	into STRICT	ie_acm_w,
			ie_se_necessario_w,
			nr_etapas_w,
			ie_status_w
	from	prescr_solucao
	where	nr_prescricao 	= nr_prescricao_p
	and		nr_seq_solucao	= nr_seq_solucao_p;

	select	count(*)
	into STRICT	qt_etapas_pend_w
	from	prescr_mat_hor b where		obter_se_horario_liberado(b.dt_lib_horario, b.dt_horario) = 'S'
	and		coalesce(b.ie_horario_especial,'N') = 'N'
	and		coalesce(b.dt_fim_horario::text, '') = ''
	and		coalesce(b.dt_suspensao::text, '') = ''
	and		b.nr_seq_solucao = nr_seq_solucao_p
	and		b.nr_prescricao = nr_prescricao_p LIMIT 1;

	if (qt_etapas_pend_w = 0) and
		((ie_acm_w = 'S') or (ie_se_necessario_w = 'S')) and (coalesce(nr_etapas_w::text, '') = '') and (ie_status_w = 'INT') then
		ie_retorno_w := 'S';
	end if;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
