CREATE OR REPLACE FUNCTION obter_se_sol_alto_custo (nr_prescricao_p bigint, nr_seq_solucao_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
ie_classif_custo_w	varchar(1);
c01 CURSOR FOR
SELECT	a.ie_classif_custo
FROM	material_estab a,
		prescr_material b
WHERE	a.cd_estabelecimento 	= cd_estabelecimento_p
AND		a.cd_material			= b.cd_material
AND		b.nr_sequencia_solucao 	= nr_seq_solucao_p
AND		b.nr_prescricao		  	= nr_prescricao_p
order by
		a.cd_material;

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
	open c01;
	loop
	fetch c01 into ie_classif_custo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			if (ie_classif_custo_w IN ('A', '1')) then
				ie_retorno_w := 'S';
				exit;
			end if;
		end;
	end loop;
	close c01;
	return coalesce(ie_retorno_w,'N');
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
