CREATE OR REPLACE FUNCTION obter_se_sol_alto_risco ( nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
ie_alto_risco_w	varchar(1);
c01 CURSOR FOR
SELECT	coalesce(a.ie_alto_risco,'N')
FROM	material a,
		prescr_material b
WHERE	a.cd_material			= b.cd_material
AND		b.nr_sequencia_solucao 	= nr_seq_solucao_p
AND		b.nr_prescricao		  	= nr_prescricao_p
order by
	a.cd_material;

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then
	open c01;
	loop
	fetch c01 into ie_alto_risco_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			if (ie_alto_risco_w = 'S') then
				ie_retorno_w := 'S';
				exit;
			end if;
		end;
	end loop;
	close c01;
	return coalesce(ie_retorno_w,'N');
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
