CREATE OR REPLACE FUNCTION obter_se_sol_atrasada ( nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);
qt_hor_validade_w bigint;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then
	qt_hor_validade_w := Obter_Param_Usuario(1113, 589, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, qt_hor_validade_w);

	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	prescr_solucao a,
			prescr_medica b
	where	a.nr_prescricao = b.nr_prescricao
	and	b.nr_prescricao = nr_prescricao_p
	and	a.nr_seq_solucao = nr_seq_solucao_p
	and	coalesce(obter_status_solucao_prescr(1, a.nr_prescricao, a.nr_seq_solucao),'N') in ('N','P')
	and	coalesce(a.ie_suspenso,'N') = 'N'
	and	coalesce(a.dt_suspensao::text, '') = ''
	and (((coalesce(a.ie_acm,'N') = 'N') and (coalesce(a.ie_se_necessario,'N') = 'N')) or
		 (((coalesce(a.ie_acm,'N') = 'S') or (coalesce(a.ie_se_necessario,'N') = 'S')) and (obter_se_sol_aprazada(nr_prescricao_p,nr_seq_solucao_p) = 'S')))
	and (obter_primeiro_hor_sol(Obter_prim_horario_sol(a.hr_prim_horario,a.dt_status), b.dt_inicio_prescr, CASE WHEN a.ie_urgencia='N' THEN  CASE WHEN a.ie_etapa_especial='N' THEN  'N'  ELSE a.ie_etapa_especial END   ELSE a.ie_urgencia END ) + qt_hor_validade_w/24)  < clock_timestamp();

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
