SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_sol_inco_farm (nr_prescricao_p bigint, nr_seq_solucao_p bigint) RETURNS varchar AS $body$
DECLARE


qt_registro_w	bigint;
ie_retorno_w	varchar(2);

BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') then
	--- Verifica se possui algum horário já gerado.
	select	count(*)
	into STRICT	qt_registro_w
	from	prescr_mat_hor
	where	nr_prescricao = nr_prescricao_p
	and	nr_seq_solucao = nr_seq_solucao_p
	and	(dt_lib_horario IS NOT NULL AND dt_lib_horario::text <> '');
	if (qt_registro_w  = 0) then
		--- Verifica se possui inconsistencia
		select	count(*)
		into STRICT	qt_registro_w
		from	prescr_material
		where	nr_prescricao = nr_prescricao_p
		and	nr_sequencia_solucao = nr_seq_solucao_p
		and	(nr_seq_inconsistencia IS NOT NULL AND nr_seq_inconsistencia::text <> '');

		if (qt_registro_w > 0) then
			ie_retorno_w := 'S';
		else
			ie_retorno_w := 'N';
		end if;
	else
		ie_retorno_w := 'N';
	end if;
end if;

return coalesce(ie_retorno_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
