SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_sol_prim_check ( nr_prescricao_p bigint, nr_seq_solucao_p bigint, ie_tipo_item_p text default null) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(1);

c01 CURSOR FOR
	SELECT	a.cd_material
	from	prescr_material a,
			material b
	where	a.nr_prescricao = nr_prescricao_p
	and		a.nr_sequencia_solucao = nr_seq_solucao_p
	and		a.cd_material = b.cd_material
	and		a.ie_agrupador in (4, 13);
	
BEGIN

if	((coalesce(ie_tipo_item_p, 'SOL') = 'SOL') or (coalesce(ie_tipo_item_p, 'SOL') = 'DI')) then
	ie_retorno_w := 'N';
	
	for r_c01 in c01 loop
		begin
		ie_retorno_w	:= coalesce(obter_se_medic_dupla_chec(r_c01.cd_material, wheb_usuario_pck.get_cd_estabelecimento, nr_prescricao_p),'N');
		
		if (ie_retorno_w = 'S') then
			exit;
		end if;
		end;
	end loop;	

	if (ie_retorno_w = 'N') then
		select	coalesce(max('S'),'N')
		into STRICT	ie_retorno_w
		from	prescr_material a,
				material b
		where	a.nr_prescricao = nr_prescricao_p
		and		a.nr_sequencia_solucao = nr_seq_solucao_p
		and		a.cd_material = b.cd_material
		and		a.ie_agrupador in (4, 13)
		and		coalesce(b.ie_dupla_checagem,'N') = 'S';
	end if;
	
elsif (coalesce(ie_tipo_item_p, 'SOL') = 'HM') then

	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	prescr_procedimento a,
		procedimento b
	where	a.nr_prescricao = nr_prescricao_p
	and	a.nr_sequencia = nr_seq_solucao_p
	and	a.cd_procedimento = b.cd_procedimento
	and	a.ie_origem_proced = b.ie_origem_proced
	and	coalesce(b.ie_dupla_checagem,'N') = 'S';
else
	
	select	coalesce(max('S'),'N')
	into STRICT	ie_retorno_w
	from	prescr_material a,
			material b
	where	a.nr_prescricao = nr_prescricao_p
	and		a.nr_sequencia = nr_seq_solucao_p
	and		a.cd_material = b.cd_material
	and		a.ie_agrupador = 8
	and		coalesce(b.ie_dupla_checagem,'N') = 'S';

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
