CREATE OR REPLACE FUNCTION obter_se_soma_dirf (nr_titulo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_titulo_w	varchar(2);
retorno_w		varchar(1);
qt_registros_w		bigint;


BEGIN

select	ie_tipo_titulo
into STRICT	ie_tipo_titulo_w
from	titulo_pagar
where	nr_titulo = nr_titulo_p;

select	count(*)
into STRICT	qt_registros_w
from	dirf_regra_tipo_tit r
where	r.ie_tipo_titulo = ie_tipo_titulo_w
and	ie_somar_imposto = 'S';

if (qt_registros_w > 0) then
	retorno_w := 'S';
else
	retorno_w := 'N';
end if;

return retorno_w;

end;
$body$
