CREATE OR REPLACE FUNCTION obter_se_soma_volume ( nr_seq_nut_pac_p nut_pac.nr_sequencia%type, nr_seq_nut_elem_mat_p nut_pac_elem_mat.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_somar_volume_w	nut_pac_elem_mat.ie_somar_volume%type;

BEGIN

select	coalesce(c.ie_somar_volume,'X')
into STRICT	ie_somar_volume_w
from	nut_pac_elem_mat c
where	c.nr_sequencia		= nr_seq_nut_elem_mat_p
and		c.nr_seq_nut_pac	= nr_seq_nut_pac_p;

if (ie_somar_volume_w = 'X') then
	select	coalesce(max(d.ie_somar_volume),'S')
	into STRICT	ie_somar_volume_w
	from	nut_pac_elem_mat a,
			nut_elem_material b,
			nut_pac_elemento c,
			nut_elemento d
	where	a.cd_material 		= b.cd_material
	and		a.nr_seq_nut_pac 	= c.nr_seq_nut_pac
	and		b.nr_seq_elemento 	= c.nr_seq_elemento
	and		b.nr_seq_elemento 	= d.nr_sequencia
	and		c.nr_seq_elemento 	= d.nr_sequencia
	and		a.nr_seq_nut_pac 	= nr_seq_nut_pac_p
	and		a.nr_sequencia		= nr_seq_nut_elem_mat_p;
end if;

return ie_somar_volume_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
