CREATE OR REPLACE FUNCTION obter_se_status_anexo_agenda ( nr_sequencia_p bigint, nr_seq_status_p bigint, cd_pessoa_fisica_p text, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'S';
nr_seq_status_depend_w	bigint;
ie_regra_status_w		varchar(1);
nr_seq_status_w			bigint;

C01 CURSOR FOR
SELECT	nr_seq_status_depend
from	status_dependencia_agenda
where	nr_seq_status = nr_seq_status_p;


BEGIN

select	max(nr_seq_status)
into STRICT	nr_seq_status_w
from	anexo_agenda
where	nr_sequencia	=	nr_sequencia_p;


open C01;
	loop
		fetch C01 into
			nr_seq_status_depend_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
		select	coalesce(max('S'),'N')
		into STRICT	ds_retorno_w
		from	anexo_agenda_hist
		where	nr_seq_anexo		=	nr_sequencia_p
		and		nr_seq_status_ant	=	nr_seq_status_depend_w;

		if (nr_seq_status_w = nr_seq_status_depend_w) then
			ds_retorno_w	:= 'S';
		end if;

		if (ds_retorno_w = 'S') then
			exit;
		end if;
	end loop;
close C01;

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_regra_status_w
from	lib_status_anexo_agenda
where	nr_seq_status_agenda = nr_seq_status_p;

if (ie_regra_status_w = 'S') and (ds_retorno_w = 'S') then
	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	lib_status_anexo_agenda
	where	coalesce(cd_pessoa_fisica,coalesce(cd_pessoa_fisica_p,'0'))		= coalesce(cd_pessoa_fisica_p,'0')
	and	coalesce(cd_setor_atendimento, coalesce(cd_setor_atendimento_p,0))	= coalesce(cd_setor_atendimento_p,0)
	and	nr_seq_status_agenda 				= nr_seq_status_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
