CREATE OR REPLACE FUNCTION obter_se_status_beira_leito (nr_seq_lote_p bigint) RETURNS varchar AS $body$
DECLARE

				
ie_retorno_w		varchar(1);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_retorno_w
from	prescr_mat_hor a,
		prescr_material b,
        ap_lote_item c
where	c.nr_seq_mat_hor    = a.nr_sequencia
and     a.nr_prescricao 	= b.nr_prescricao
and		a.nr_seq_material 	= b.nr_sequencia
and		b.ie_agrupador		in (1,4,12,8,10,11)
and		a.ie_checado_leito	= 'S'
and		c.nr_seq_lote 		= nr_seq_lote_p;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
