CREATE OR REPLACE FUNCTION obter_se_status_cirur_paciente (nr_cirurgia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ie_status_cirurgia_w	varchar(3);
ds_retorno_w			varchar(1):='N';

/*
ie_opcao_p
C - Cancelada
P - Prevista
I - Interrompida
R - Realizada
CI - Cancelada ou interrompida
PR - Prevista ou realizada
*/
BEGIN

select	coalesce(max(ie_status_cirurgia),-1)
into STRICT	ie_status_cirurgia_w
from	cirurgia
where	nr_cirurgia = nr_cirurgia_p;

if (ie_status_cirurgia_w = 1) and (ie_opcao_p = 'P') then
	ds_retorno_w := 'S';
elsif (ie_status_cirurgia_w = 2) and (ie_opcao_p = 'R') then
	ds_retorno_w := 'S';
elsif (ie_status_cirurgia_w = 3) and (ie_opcao_p = 'C') then
	ds_retorno_w := 'S';
elsif (ie_status_cirurgia_w = 4) and (ie_opcao_p = 'I') then
	ds_retorno_w := 'S';
elsif	((ie_status_cirurgia_w = 1) or (ie_status_cirurgia_w = 2)) and (ie_opcao_p = 'PR') then
	ds_retorno_w := 'S';
elsif	((ie_status_cirurgia_w = 3) or (ie_status_cirurgia_w = 4)) and (ie_opcao_p = 'CI') then
	ds_retorno_w := 'S';
end if;

return 	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
