CREATE OR REPLACE FUNCTION obter_se_status_conta_etapa (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_etapa_w		varchar(80)	:= null;
nr_seq_etapa_w		bigint	:= null;
ds_retorno_w		varchar(255)	:= null;
ds_observacao_w		varchar(255);
ie_muda_status_conta_w	varchar(01);
nr_interno_conta_w	bigint;

c02 CURSOR FOR 
	SELECT	a.nr_interno_conta 
	from	conta_paciente a 
	where	a.nr_atendimento = nr_atendimento_p 
	and	a.ie_status_acerto = 1 
	order by a.nr_interno_conta;

 
 
c01 CURSOR FOR 
	SELECT	a.nr_seq_etapa, 
		b.ds_etapa, 
		substr(a.ds_observacao,1,255), 
		coalesce(ie_muda_status_conta, 'S') 
	from	fatur_etapa b, 
		conta_paciente_etapa a 
	where	a.nr_seq_etapa	= b.nr_sequencia 
	and	a.nr_interno_conta = nr_interno_conta_w 
	and	coalesce(a.dt_fim_etapa::text, '') = '' 
	and	coalesce(b.ie_situacao,'A')= 'A' 
	order by a.dt_etapa;

 

BEGIN 
 
ds_retorno_w:= 'S';
 
open C02;
loop 
fetch C02 into	 
	nr_interno_conta_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin 
	 
	open	c01;
	loop 
	fetch	c01 into 
		nr_seq_etapa_w, 
		ds_etapa_w, 
		ds_observacao_w, 
		ie_muda_status_conta_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin 
		 
		ds_retorno_w	:= ie_muda_status_conta_w;
		 
		if (coalesce(ds_retorno_w,'S') = 'N') then 
			goto final;
			--exit; 
		end if;
 
		end;
	end loop;
	close c01;
		 
	end;
end loop;
close C02;
 
<<final>> 
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
