CREATE OR REPLACE FUNCTION obter_se_status_laudo_lib ( ie_status_laudo_p text, cd_setor_atendimento_p bigint, cd_perfil_p bigint, nr_seq_interno_prescr_p bigint default null, nm_usuario_p text default null) RETURNS varchar AS $body$
DECLARE


qt_reg_w		bigint;
cd_tipo_procedimento_w	bigint;
nr_seq_perfil_pep_w	bigint := null;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	regra_vis_laudo_status
where	ie_status_laudo	= ie_status_laudo_p;

if (qt_reg_w	> 0) then


	select	coalesce(max(nr_seq_perfil),0)
	into STRICT	nr_seq_perfil_pep_w
	from	usuario
	where	nm_usuario = nm_usuario_p;


	if (nr_seq_interno_prescr_p IS NOT NULL AND nr_seq_interno_prescr_p::text <> '') then

		select	coalesce(max(a.cd_tipo_procedimento),0)
		into STRICT	cd_tipo_procedimento_w
		from	prescr_procedimento b,
			procedimento a
		where	b.nr_seq_interno	= nr_seq_interno_prescr_p
		and	a.cd_procedimento	= b.cd_procedimento
		and	a.ie_origem_proced	= b.ie_origem_proced;

	end if;

	select	count(*)
	into STRICT	qt_reg_w
	from	regra_vis_laudo_status
	where	ie_status_laudo	= ie_status_laudo_p
	and	coalesce(cd_setor_atendimento,cd_setor_atendimento_p)	= cd_setor_atendimento_p
	and	coalesce(cd_perfil,cd_perfil_p)				= cd_perfil_p
	and	coalesce(cd_tipo_procedimento,cd_tipo_procedimento_w)	= cd_tipo_procedimento_w
	and	coalesce(nr_seq_perfil,nr_seq_perfil_pep_w)			= nr_seq_perfil_pep_w;

	if (qt_reg_w	= 0) then
		return 'N';
	end if;

end if;

return	'S';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
