CREATE OR REPLACE FUNCTION obter_se_status_leito_saida (nr_atend_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_status_unidade_w	varchar(3);
cd_setor_atual_w	varchar(255);
cd_unidade_basica_w	varchar(255);
cd_unidade_compl_w	varchar(255);


BEGIN 
 
select	obter_unidade_atendimento(nr_atend_p, 'A', 'CS'), 
	obter_unidade_atendimento(nr_atend_p, 'A', 'UB'), 
	obter_unidade_atendimento(nr_atend_p, 'A', 'UC') 
into STRICT	cd_setor_atual_w,	 
	cd_unidade_basica_w, 
	cd_unidade_compl_w
;
 
select	ie_status_unidade 
into STRICT	ie_status_unidade_w 
from	unidade_atendimento 
where	cd_setor_atendimento = cd_setor_atual_w 
and	cd_unidade_basica  = cd_unidade_basica_w 
and	cd_unidade_compl   = cd_unidade_compl_w;
 
 
 
return	ie_status_unidade_w;
 
end;
$body$
