CREATE OR REPLACE FUNCTION obter_se_status_produto ( nr_seq_status_p bigint, nr_lote_producao_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';


BEGIN

if (nr_seq_status_p IS NOT NULL AND nr_seq_status_p::text <> '') and (nr_lote_producao_p IS NOT NULL AND nr_lote_producao_p::text <> '')  then
	select	coalesce(MAX('S'),'N')
	into STRICT	ie_retorno_w
	from    lote_producao_comp a
	where   a.nr_lote_producao = nr_lote_producao_p
	and	nr_seq_estagio 	   = nr_seq_status_p;

end if;


return ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
