CREATE OR REPLACE FUNCTION obter_se_subamostra_exec (nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE

qt_exec_w integer:=0;


BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '' AND nr_seq_prescr_p IS NOT NULL AND nr_seq_prescr_p::text <> '') then

	SELECT 	1
	into STRICT	qt_exec_w
	
	where	exists (SELECT 1
					FROM   	PEDIDO_SUBAMOSTRA
					WHERE  	nr_prescricao 	= nr_prescricao_p
					AND	nr_seq_prescr 		= nr_seq_prescr_p
					and	(dt_exec_pedido IS NOT NULL AND dt_exec_pedido::text <> ''));

	if qt_exec_w > 0 then
		return	'S';
	else
		return 	'N';
	end if;
end if;
exception
when others then
	return 'N';
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
