CREATE OR REPLACE FUNCTION obter_se_subprojeto_recurso ( nr_seq_projeto_p bigint) RETURNS varchar AS $body$
DECLARE



ie_subprojeto_w			varchar(1) := 'N';
ie_tipo_projeto_w		projeto_recurso.ie_tipo_projeto%type;


BEGIN

select	coalesce(ie_tipo_projeto,'P')
into STRICT	ie_tipo_projeto_w
from	projeto_recurso
where	nr_sequencia = nr_seq_projeto_p;

if (ie_tipo_projeto_w = 'S') then
	ie_subprojeto_w := 'S';
end if;

return	ie_subprojeto_w;

end;
$body$
LANGUAGE PLPGSQL
