CREATE OR REPLACE FUNCTION obter_se_susp_item_lib_pend ( cd_estabelecimento_p bigint, nr_atendimento_p bigint, ie_tipo_item_p text, cd_item_p text, nr_prescricoes_p text, nr_seq_item_p bigint, nm_usuario_p text, nr_seq_cpoe_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w	varchar(1) := 'N';
nr_prescricao_w		prescr_medica.nr_prescricao%type;
nr_prescricoes_w	varchar(255) := '';


BEGIN

nr_prescricoes_w	:= nr_prescricoes_p;

	if (ie_tipo_item_p in ('S', 'SNE', 'M', 'IAH', 'MAT')) then --Medicamento pendentes
		if (ie_tipo_item_p in ('M', 'IAH', 'MAT')) then
			if ((coalesce(nr_prescricoes_w::text, '') = '') and (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '')) then
				select 	max(nr_prescricao)
				into STRICT	nr_prescricao_w
				from	prescr_material
				where	nr_seq_mat_cpoe = nr_seq_cpoe_p;
				nr_prescricoes_w	:=  nr_prescricao_w;
			end if;
		else
			if ((coalesce(nr_prescricoes_w::text, '') = '') and (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '')) then
				select 	max(nr_prescricao)
				into STRICT	nr_prescricao_w
				from	prescr_material
				where	nr_seq_dieta_cpoe = nr_seq_cpoe_p;
				nr_prescricoes_w	:=  nr_prescricao_w;
			end if;
		end if;

		select	coalesce(max('S'),'N')
		into STRICT	ie_permite_w
		from	prescr_material b,
			prescr_medica a where	b.nr_prescricao = a.nr_prescricao
		and	a.nr_atendimento = nr_atendimento_p
		--and	b.ie_agrupador in (1,12)
		and	coalesce(b.ie_suspenso,'N') <> 'S'
		and	a.dt_validade_prescr > clock_timestamp()
		and	b.cd_material = cd_item_p
		and	obter_se_valor_contido(a.nr_prescricao, nr_prescricoes_w) = 'S' LIMIT 1;

	elsif (ie_tipo_item_p in ('P', 'L', 'HM')) then --Procedimentos pendentes
		if ((coalesce(nr_prescricoes_w::text, '') = '') and (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '')) then
			select 	max(nr_prescricao)
			into STRICT	nr_prescricao_w
			from	prescr_procedimento
			where	nr_seq_proc_cpoe = nr_seq_cpoe_p;
			nr_prescricoes_w	:=  nr_prescricao_w;
		end if;

		select	coalesce(max('S'),'N')
		into STRICT	ie_permite_w
		from	prescr_procedimento b,
			prescr_medica a where	b.nr_prescricao = a.nr_prescricao
		and	a.nr_atendimento = nr_atendimento_p
		and	coalesce(b.ie_suspenso,'N') <> 'S'
		and	a.dt_validade_prescr > clock_timestamp()
		and	b.cd_procedimento = cd_item_p
		and	obter_se_valor_contido(a.nr_prescricao, nr_prescricoes_w) = 'S' LIMIT 1;
	elsif (ie_tipo_item_p = 'SOL') then --Soluções pendentes
		if ((coalesce(nr_prescricoes_w::text, '') = '') and (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '')) then
			select 	max(nr_prescricao)
			into STRICT	nr_prescricao_w
			from	prescr_material
			where	nr_seq_mat_cpoe = nr_seq_cpoe_p;
			nr_prescricoes_w	:=  nr_prescricao_w;
		end if;

		select	coalesce(max('S'),'N')
		into STRICT	ie_permite_w
		from	prescr_solucao a,
			prescr_medica b where	a.nr_prescricao = b.nr_prescricao
		and	b.nr_atendimento = nr_atendimento_p
		and	coalesce(a.ie_suspenso,'N') <> 'S'
		and	b.dt_validade_prescr > clock_timestamp()
		and	a.nr_seq_solucao = nr_seq_item_p
		and	obter_se_valor_contido(b.nr_prescricao, nr_prescricoes_w) = 'S' LIMIT 1;
	elsif (ie_tipo_item_p = 'O') then --Gasoterapia
		if ((coalesce(nr_prescricoes_w::text, '') = '') and (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '')) then
			select 	max(nr_prescricao)
			into STRICT	nr_prescricao_w
			from	prescr_gasoterapia
			where	nr_seq_gas_cpoe = nr_seq_cpoe_p;
			nr_prescricoes_w	:=  nr_prescricao_w;
		end if;

		select	coalesce(max('S'),'N')
		into STRICT	ie_permite_w
		from	prescr_gasoterapia a,
			prescr_medica b where	a.nr_prescricao = b.nr_prescricao
		and	b.nr_atendimento = nr_atendimento_p
		and	coalesce(a.ie_suspenso,'N') <> 'S'
		and	b.dt_validade_prescr > clock_timestamp()
		and	a.nr_sequencia = nr_seq_item_p
		and	obter_se_valor_contido(b.nr_prescricao, nr_prescricoes_w) = 'S' LIMIT 1;
	elsif (ie_tipo_item_p = 'R') then --Recomendação
		if ((coalesce(nr_prescricoes_w::text, '') = '') and (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '')) then
			select 	max(nr_prescricao)
			into STRICT	nr_prescricao_w
			from	prescr_recomendacao
			where	nr_seq_rec_cpoe = nr_seq_cpoe_p;
			nr_prescricoes_w	:=  nr_prescricao_w;
		end if;

		select	coalesce(max('S'),'N')
		into STRICT	ie_permite_w
		from	prescr_recomendacao a,
			prescr_medica b where	a.nr_prescricao = b.nr_prescricao
		and	b.nr_atendimento = nr_atendimento_p
		and	coalesce(a.ie_suspenso,'N') <> 'S'
		and	b.dt_validade_prescr > clock_timestamp()
		and	a.cd_recomendacao = cd_item_p
		and	obter_se_valor_contido(b.nr_prescricao, nr_prescricoes_w) = 'S' LIMIT 1;
	elsif (ie_tipo_item_p = 'P') then --Coleta
		if ((coalesce(nr_prescricoes_w::text, '') = '') and (nr_seq_cpoe_p IS NOT NULL AND nr_seq_cpoe_p::text <> '')) then
			select 	max(nr_prescricao)
			into STRICT	nr_prescricao_w
			from	prescr_procedimento
			where	nr_seq_proc_cpoe = nr_seq_cpoe_p;
			nr_prescricoes_w	:=  nr_prescricao_w;
		end if;

		select	coalesce(max('S'),'N')
		into STRICT	ie_permite_w
		from	prescr_procedimento a,
			prescr_medica b where	a.nr_prescricao = b.nr_prescricao
		and	b.nr_atendimento = nr_atendimento_p
		and	coalesce(a.ie_suspenso,'N') <> 'S'
		and	b.dt_validade_prescr > clock_timestamp()
		and	a.cd_procedimento = cd_item_p
		and	obter_se_valor_contido(b.nr_prescricao, nr_prescricoes_w) = 'S' LIMIT 1;
	end if;


return ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
