CREATE OR REPLACE FUNCTION obter_se_susp_transfer_medic ( cd_setor_atendimento_p bigint, cd_material_p bigint, ie_protocolo_p text default 'N') RETURNS varchar AS $body$
DECLARE

								
nr_seq_regra_w CPOE_RULE_SUSP_TRANSFER.NR_SEQUENCIA%TYPE;
nr_seq_regra_medic_w	CPOE_RULE_SUSP_ITEMS.NR_SEQUENCIA%TYPE;
cont_rule_susp_proc_w integer;
qt_regra_espec_medic_w CPOE_RULE_SUSP_PROC.NR_SEQUENCIA%TYPE;
ds_retorno_w varchar(1) := 'N';
								

BEGIN

select	max(a.nr_sequencia)
into STRICT	nr_seq_regra_w
from	CPOE_RULE_SUSP_TRANSFER a
where	a.CD_SETOR_ATENDIMENTO = cd_setor_atendimento_p
and		a.IE_SITUACAO = 'S';

if (nr_seq_regra_w > 0) then -- if there is rule for the department
	
	select	b.nr_sequencia
	into STRICT	nr_seq_regra_medic_w
	from	CPOE_RULE_SUSP_ITEMS b
	where	b.NR_SEQ_RULE_SUSP = nr_seq_regra_w
	and		b.IE_GRUPO = 'M'
	and		b.IE_SITUACAO = 'S';
	
	if (nr_seq_regra_medic_w > 0) then
	
		ds_retorno_w := 'S';
	
		select	count(*)
		into STRICT	cont_rule_susp_proc_w
		from	CPOE_RULE_SUSP_MEDIC c
		where	c.NR_SEQ_RULE_SUSP_ITEMS = nr_seq_regra_medic_w
		and		c.IE_SITUACAO = 'S';
		
		if (cont_rule_susp_proc_w > 0) then
			
			select	count(*)
			into STRICT	qt_regra_espec_medic_w
			from 	CPOE_RULE_SUSP_MEDIC d
			where	d.NR_SEQ_RULE_SUSP_ITEMS = nr_seq_regra_medic_w
			and		d.ie_situacao = 'S'
			and		((coalesce(d.CD_MATERIAL::text, '') = '') or (d.CD_MATERIAL = cd_material_p))
			and 	((coalesce(d.CD_GRUPO_MATERIAL::text, '') = '') or (d.CD_GRUPO_MATERIAL = Obter_estrutura_material(cd_material_p, 'G')))
			and 	((coalesce(d.CD_SUBGRUPO_MATERIAL::text, '') = '') or (d.CD_SUBGRUPO_MATERIAL = Obter_estrutura_material(cd_material_p, 'S')))
			and 	((coalesce(d.CD_CLASSE_MATERIAL::text, '') = '') or (d.CD_CLASSE_MATERIAL = Obter_estrutura_material(cd_material_p, 'C')))
			and     ((coalesce(d.ie_somente_protocolo, 'N') = 'N') or (coalesce(d.ie_somente_protocolo, 'N') = 'S' and ie_protocolo_p = 'S'));
			
			if (qt_regra_espec_medic_w = 0) then
				ds_retorno_w := 'N';
			end if;
			
		end if;
	
	end if;
	
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
