CREATE OR REPLACE FUNCTION obter_se_tab_preco_simulacao ( nr_seq_tabela_p bigint) RETURNS varchar AS $body$
DECLARE


ie_simulacao_w	varchar(1) := 'N';


BEGIN
if (nr_seq_tabela_p IS NOT NULL AND nr_seq_tabela_p::text <> '') then
	begin
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_simulacao_w
	from	pls_tabela_preco
	where	nr_sequencia = nr_seq_tabela_p
	and	pls_obter_se_tabela_simulacao(nr_sequencia) = 'S';
	end;
end if;
return ie_simulacao_w;
end;
$body$
