SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_tela_val_prescr (cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_exibe_tela_w		varchar(1) := 'S';
hr_inicial_w		varchar(5);
hr_final_w			varchar(5);
dt_ini_w			timestamp;
dt_fim_w			timestamp;


BEGIN

if (coalesce(cd_estabelecimento_p,0) > 0) then
	select 	max(hr_inicial_tela),
			max(hr_final_tela)
	into STRICT	hr_inicial_w,
			hr_final_w
	from	parametro_medico
	where	cd_estabelecimento = cd_estabelecimento_p;

	if (hr_inicial_w IS NOT NULL AND hr_inicial_w::text <> '') and (hr_final_w IS NOT NULL AND hr_final_w::text <> '') then

		dt_ini_w := to_date(to_char(clock_timestamp(),'dd/mm/yyyy ') || hr_inicial_w || ':00','dd/mm/yyyy hh24:mi:ss');
		dt_fim_w := to_date(to_char(clock_timestamp(),'dd/mm/yyyy ') || hr_final_w   || ':00','dd/mm/yyyy hh24:mi:ss');

		select	coalesce(max('S'),'N')
		into STRICT	ie_exibe_tela_w
		
		where	clock_timestamp() between dt_ini_w and dt_fim_w;
	end if;

end if;

return	ie_exibe_tela_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
