CREATE OR REPLACE FUNCTION obter_se_tem_medic (cd_material_p bigint, ds_medicamento_p text, nr_seq_versao_atual_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w varchar(1);


BEGIN

  	select 	coalesce(max('S'),'N')
	into STRICT 	ds_retorno_w
	from	paciente_medic_uso
	where	nr_seq_versao = nr_seq_versao_atual_p
	and (cd_material = cd_material_p
	or (coalesce(cd_material::text, '') = '' and ds_medicamento = ds_medicamento_p));

	return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
