CREATE OR REPLACE FUNCTION obter_se_tem_observacao (nr_seq_paciente_p bigint) RETURNS varchar AS $body$
DECLARE



retorno_w varchar(1) := 'N';

ie_obrigatorio_w varchar(30);


BEGIN

    ie_obrigatorio_w := Obter_Regra_Atributo(cd_estabelecimento_p => wheb_usuario_pck.get_cd_estabelecimento,
					cd_perfil_p => wheb_usuario_pck.get_cd_perfil,
					nm_usuario_p => wheb_usuario_pck.get_nm_usuario,
					nm_tabela_p => 'PACIENTE_PROTOCOLO_MEDIC',
					nm_atributo_p => 'DS_OBSERVACAO',
					ie_regra_p => 'O',
					ds_regra_p => null,
					nr_seq_regra_p => 0,
					nr_seq_visao_p => 0,
					cd_funcao_p => 281);

    if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') and (ie_obrigatorio_w = 'S') then

        select  coalesce(max('S'), 'N')
        into STRICT    retorno_w
        from    paciente_protocolo_medic
        where   nr_seq_paciente = nr_seq_paciente_p
        and     coalesce(ds_observacao::text, '') = '';
    end if;

    return retorno_w;

end;
$body$
