CREATE OR REPLACE FUNCTION obter_se_terceiro_escala ( nr_seq_escala_dir_p bigint, nr_seq_terceiro_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1)	:= 'S';


BEGIN

if (nr_seq_terceiro_p IS NOT NULL AND nr_seq_terceiro_p::text <> '') then

	select	coalesce(max('S'),'N')
	into STRICT	ds_retorno_w
	from	escala_diaria_adic
	where	nr_seq_escala_diaria	= nr_seq_escala_dir_p
	and	obter_se_pessoa_terceiro(cd_pessoa_fisica, nr_seq_terceiro_p) = 'S';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
