CREATE OR REPLACE FUNCTION obter_se_terceiro_pessoa (cd_pessoa_fisica_p text, nr_seq_terceiro_p bigint, dt_procedimento_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);	
nr_seq_terceiro_w	terceiro.nr_sequencia%type;	
qt_reg_w		bigint := 0;
		
			

BEGIN
ds_retorno_w	:= 'S';

if (nr_seq_terceiro_p IS NOT NULL AND nr_seq_terceiro_p::text <> '') then
	begin
	ds_retorno_w	:= 'N';	
	
	select	count(1)
	into STRICT	qt_reg_w
	from 	terceiro
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	(cd_pessoa_fisica IS NOT NULL AND cd_pessoa_fisica::text <> '')
	and	nr_sequencia		= nr_seq_terceiro_p
	and	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_procedimento_p) between ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(coalesce(dt_inicio_vigencia,dt_procedimento_p)) and
                                                                         ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(coalesce(dt_fim_vigencia,dt_procedimento_p))  LIMIT 1;
	
	if (qt_reg_w = 0) then
		begin
		
		select 	count(1)
		into STRICT	qt_reg_w
		from 	terceiro_pessoa_fisica
		where	cd_pessoa_fisica	= cd_pessoa_fisica_p
		and	nr_seq_terceiro		= nr_seq_terceiro_p
		and	ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(dt_procedimento_p) between ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(coalesce(dt_inicio_vigencia,dt_procedimento_p)) and
                                                                           ESTABLISHMENT_TIMEZONE_UTILS.startOfDay(coalesce(dt_fim_vigencia,dt_procedimento_p))  LIMIT 1;
		
		if (qt_reg_w > 0) then
			ds_retorno_w	:= 'S';	
		end if;
		
		end;
	else
		ds_retorno_w	:= 'S';	
	end if;

	end;
end if;	

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
