CREATE OR REPLACE FUNCTION obter_se_terceiro_regra_esp ( nr_seq_regra_esp_p bigint, nr_seq_terceiro_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
nr_seq_terceiro_w	bigint;
qt_terc_regra_w		bigint;


BEGIN

select	a.nr_seq_terceiro
into STRICT	nr_seq_terceiro_w
from	regra_esp_repasse a
where	a.nr_sequencia	= nr_seq_regra_esp_p;

ds_retorno_w	:= 'N';

if (nr_seq_terceiro_w IS NOT NULL AND nr_seq_terceiro_w::text <> '') and (nr_seq_terceiro_w	= nr_seq_terceiro_p) then
	ds_retorno_w	:= 'S';
else

	select	count(*)
	into STRICT	qt_terc_regra_w
	from	regra_esp_repasse_terc a
	where	a.nr_seq_regra_esp	= nr_seq_regra_esp_p;

	if (qt_terc_regra_w	= 0) then

		if (coalesce(nr_seq_terceiro_w::text, '') = '') then
			ds_retorno_w	:= 'S';
		end if;

	else

		select	coalesce(max('S'),'N')
		into STRICT	ds_retorno_w
		from	regra_esp_repasse_terc a
		where	a.nr_seq_terceiro	= nr_seq_terceiro_p
		and	a.nr_seq_regra_esp	= nr_seq_regra_esp_p;

	end if;

end if;

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
