CREATE OR REPLACE FUNCTION obter_se_terc_pf_conv (nr_seq_terceiro_p bigint, cd_pessoa_fisica_p text, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


cont_w		bigint;
ie_retorno_w	varchar(255);


BEGIN

ie_retorno_w		:= 'S';

select	count(*)
into STRICT	cont_w
from	terceiro_pf_conv
where	nr_seq_terceiro		= nr_seq_terceiro_p
and	cd_pessoa_fisica	= cd_pessoa_fisica_p;

if (cont_w > 0) then
	ie_retorno_w		:= 'N';
	select	count(*)
	into STRICT	cont_w
	from	terceiro_pf_conv
	where	nr_seq_terceiro		= nr_seq_terceiro_p
	and	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	cd_convenio		= cd_convenio_p;
	if (cont_w > 0) then
		ie_retorno_w		:= 'S';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
