CREATE OR REPLACE FUNCTION obter_se_texto_agenda_cirur ( nr_seq_texto_p bigint, nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE

qt_reg_w	bigint;
qt_reg_ww	bigint;
qt_reg_www	bigint;

BEGIN

select	count(*)
into STRICT	qt_reg_w
from	PROCEDIMENTO_TEXTO_PADRAO
where	NR_SEQ_TEXTO_PADRAO	= nr_seq_texto_p;

if (qt_reg_w	= 0) then
	return 'S';
end if;


select	count(*)
into STRICT	qt_reg_w
from	agenda_paciente a
where	nr_sequencia	= nr_seq_agenda_p
and	exists (	SELECT	1
			from	PROCEDIMENTO_TEXTO_PADRAO x
			where	a.cd_procedimento	= x.cd_procedimento
			and	a.ie_origem_proced	= x.ie_origem_proced
			and	x.NR_SEQ_TEXTO_PADRAO	= nr_seq_texto_p);

select	count(*)
into STRICT	qt_reg_ww
from	AGENDA_PACIENTE_PROC a
where	NR_SEQ_AGENDA	= nr_seq_agenda_p
and	exists (	SELECT	1
			from	PROCEDIMENTO_TEXTO_PADRAO x
			where	a.cd_procedimento	= x.cd_procedimento
			and	a.ie_origem_proced	= x.ie_origem_proced
			and	x.NR_SEQ_TEXTO_PADRAO	= nr_seq_texto_p);

select	count(*)
into STRICT	qt_reg_www
from	AGENDA_PACIENTE_PROC a
where	NR_SEQ_AGENDA	= nr_seq_agenda_p
and	exists (	SELECT	1
			from	PROCEDIMENTO_TEXTO_PADRAO x
			where	a.nr_seq_proc_interno	= x.nr_seq_proc_interno
			and	x.NR_SEQ_TEXTO_PADRAO	= nr_seq_texto_p);


if (qt_reg_ww	= 0) and (qt_reg_w	= 0) and (qt_reg_www	= 0)then
	return 'N';
end if;


return	'S';

end;
