CREATE OR REPLACE FUNCTION obter_se_texto_lib_pato (nr_seq_texto_p bigint, nr_prescricao_p bigint, nr_seq_prescr_p bigint, nr_seq_peca_p bigint, ie_tipo_p text, nr_seq_laudo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
nr_seq_amostra_princ_w	bigint;

BEGIN

if (nr_seq_peca_p IS NOT NULL AND nr_seq_peca_p::text <> '') then

	select  coalesce(max(nr_seq_amostra_princ),0)
	into STRICT	nr_seq_amostra_princ_w
	from	prescr_proc_peca
	where 	nr_sequencia = nr_seq_peca_p;

	select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	regra_texto_padrao_pat
	where	nr_seq_texto_padrao = nr_seq_texto_p
	and	coalesce(ie_tipo_texto,ie_tipo_p) = ie_tipo_p
	and	coalesce(nr_seq_amostra_princ,nr_seq_amostra_princ_w) = nr_seq_amostra_princ_w
	and	coalesce(NR_SEQ_EXAME_COMPL::text, '') = '';

elsif (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then

	select  CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	regra_texto_padrao_pat a
	where	nr_seq_texto_padrao = nr_seq_texto_p
	and	coalesce(ie_tipo_texto,ie_tipo_p) = ie_tipo_p
	and (exists (SELECT 1 from laudo_pac_exame_compl z,
				  	   prescr_proc_exame_compl w
			where nr_seq_laudo = nr_seq_laudo_p
			and z.nr_seq_exame_compl = w.nr_sequencia
			and a.nr_seq_exame_compl = w.nr_seq_pato_exame) or (coalesce(nr_seq_exame_compl::text, '') = ''))
	and	coalesce(nr_seq_amostra_princ,0) in (select 	coalesce(nr_seq_amostra_princ,0)
						from 	prescr_proc_peca a
						where	nr_prescricao = nr_prescricao_p
						and 	nr_seq_prescr = nr_seq_prescr_p);



end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
