SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_se_texto_lib ( nr_sequencia_p bigint, cd_perfil_p bigint, cd_setor_atendimento_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


cd_perfil_w		bigint;
cd_setor_w		bigint;
ie_permite_w		varchar(1)	:= 'S';
qt_reg_w			bigint;
cd_estab_logado_w	texto_padrao_sis_lib.cd_estabelecimento%type	:= wheb_usuario_pck.get_cd_estabelecimento;

c01 CURSOR FOR
	SELECT  a.ie_permite
  	from  texto_padrao_sis_lib a
  	where  a.nr_seq_texto = nr_sequencia_p
  	and  coalesce(a.cd_setor_atendimento, cd_setor_w) = cd_setor_w
  	and  coalesce(a.cd_perfil, cd_perfil_w) = cd_perfil_w
	and	coalesce(a.nm_usuario_lib, coalesce(nm_usuario_p,'0')) = coalesce(nm_usuario_p,'0')
	and (coalesce(a.cd_estabelecimento, cd_estab_logado_w) = cd_estab_logado_w /* OS 2146533 - Jeferson Job - Alteracao para atender aos grupo de empresas */
      or exists (
         SELECT 1
         from grupo_emp_estrutura g,
              estabelecimento e
         where g.cd_empresa = e.cd_empresa
         and g.ie_tipo_estrutura = 1
         and e.ie_tipo_estab = 'M'
         and e.cd_estabelecimento = cd_estab_logado_w
      ))
	and (coalesce(a.nr_seq_grupo_usuario::text, '') = ''
		or exists (		select	1
				from	usuario_grupo y,
					grupo_usuario x
				where	x.nr_sequencia = y.nr_seq_grupo
				and	y.nr_seq_grupo = a.nr_seq_grupo_usuario
				and	y.nm_usuario_grupo = nm_usuario_p
				and	x.ie_situacao = 'A'
				and	y.ie_situacao = 'A'))
	order by a.nm_usuario_lib desc,
		a.cd_perfil desc,
		a.cd_setor_atendimento desc,
		a.nr_seq_grupo_usuario desc,
		a.nr_sequencia;

BEGIN

cd_perfil_w	:= coalesce(cd_perfil_p,0);
cd_setor_w	:= coalesce(cd_setor_atendimento_p,0);

select	count(*)
into STRICT	qt_reg_w
from	texto_padrao_sis_lib
where	nr_seq_texto = nr_sequencia_p;

if (qt_reg_w > 0) then
	begin
	ie_permite_w := 'N';
	
	open C01;
	loop
	fetch C01 into	
		ie_permite_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;	
	end;
end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_se_texto_lib ( nr_sequencia_p bigint, cd_perfil_p bigint, cd_setor_atendimento_p bigint, nm_usuario_p text) FROM PUBLIC;

