CREATE OR REPLACE FUNCTION obter_se_texto_orient_vazio (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE



ds_orientacao_w	text;
ie_retorno_w	varchar(1);


BEGIN

ie_retorno_w := 'N';

begin
select	ds_orientacao
into STRICT	ds_orientacao_w
from	atendimento_alta
where	nr_sequencia	= nr_sequencia_p;
exception
when others then
	ds_orientacao_w := null;
end;

if (coalesce(ds_orientacao_w::text, '') = '') or (ds_orientacao_w = ' ') or (ds_orientacao_w = '') or (trim(both ds_orientacao_w) = '') then
	ie_retorno_w	:= 'S';
end if;

return ie_retorno_w;

end;
