CREATE OR REPLACE FUNCTION obter_se_texto_padrao_lib ( nr_sequencia_p bigint, cd_setor_atendimento_p bigint default null) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w	varchar(10)	:= 'S';
qt_registro_w	bigint;
cd_pessoa_fisica_w	varchar(10);
cd_perfil_w		bigint;
cd_setor_atendimento_w	bigint;
ie_tipo_evolucao_w		varchar(10);
cd_estabelecimento_w	smallint;
ie_situacao_w	texto_padrao.ie_situacao%type;


BEGIN 
 
select 	coalesce(max(ie_situacao),'A') 
into STRICT 	ie_situacao_w 
from 	texto_padrao 
where 	nr_sequencia = nr_sequencia_p;
 
if (ie_situacao_w = 'I') then 
	 
	ds_retorno_w := 'N';
else 
 
	select	coalesce(max(cd_pessoa_fisica),'0'), 
			coalesce(max(ie_tipo_evolucao),'X') 
	into STRICT	cd_pessoa_fisica_w, 
			ie_tipo_evolucao_w 
	from	usuario 
	where	nm_usuario	= wheb_usuario_pck.get_nm_usuario;
 
	cd_perfil_w		:= obter_perfil_ativo;
	cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;
 
	select	count(*) 
	into STRICT	qt_registro_w 
	from	texto_padrao_regra 
	where	nr_seq_texto	= nr_sequencia_p 
	and (ie_situacao = 'A' or coalesce(ie_situacao::text, '') = '');
 
	if (qt_registro_w	> 0) then 
		cd_setor_atendimento_w	:= coalesce(cd_setor_atendimento_p,0);
		select	count(*) 
		into STRICT	qt_registro_w 
		from	texto_padrao_regra 
		where	nr_seq_texto	= nr_sequencia_p 
		and	coalesce(cd_perfil,cd_perfil_w)	= cd_perfil_w 
		and	coalesce(cd_pessoa_fisica,cd_pessoa_fisica_w)	= cd_pessoa_fisica_w 
		and	((coalesce(cd_especialidade::text, '') = '') or (obter_se_especialidade_medico(cd_pessoa_fisica_w,cd_especialidade)	= 'S') ) 
		and	coalesce(cd_setor_atendimento,cd_setor_atendimento_w)	= cd_setor_atendimento_w 
		and	coalesce(ie_tipo_evolucao,ie_tipo_evolucao_w)	= ie_tipo_evolucao_w 
		and	coalesce(cd_estabelecimento,cd_estabelecimento_w) = cd_estabelecimento_w 
		and (ie_situacao = 'A' or coalesce(ie_situacao::text, '') = '');
		 
		if (qt_registro_w	= 0) then 
			ds_retorno_w	:= 'N';
		end if;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
